import {filter} from '@iterable-iterator/filter';
import {list} from '@iterable-iterator/list';
import {map} from '@iterable-iterator/map';

import Graph from './Graph.js';

const isIngoing =
	(v) =>
	([, u]) => {
		return u === v;
	};

const isOutgoing =
	(v) =>
	([t]) => {
		return t === v;
	};

const reverse = (e) => {
	const v = e.pop();
	const u = e.pop();
	e.push(v, u);
	return e;
};

export default class AbstractDirectedGraph extends Graph {
	initr(u) {
		return filter(isIngoing(u), this.E);
	}

	outitr(u) {
		return filter(isOutgoing(u), this.E);
	}

	reverse() {
		this.E = list(map(reverse, this.E));
	}
}

import {filter} from '@iterable-iterator/filter';
import {map} from '@iterable-iterator/map';
import {NotImplementedError} from '@failure-abstraction/error';

const other = ([t, u], v) => (t === v ? u : t);

export default class AbstractGraph {
	vadd(_) {
		throw new NotImplementedError();
	}

	vdel(_) {
		throw new NotImplementedError();
	}

	eadd(_u, _v) {
		throw new NotImplementedError();
	}

	edel(_) {
		throw new NotImplementedError();
	}

	vitr() {
		throw new NotImplementedError();
	}

	eitr() {
		throw new NotImplementedError();
	}

	iitr(v) {
		return filter((e) => {
			const [t, u] = this.endpoints(e);
			return t === v || u === v;
		}, this.eitr());
	}

	nitr(v) {
		return map((e) => other(this.endpoints(e), v), this.iitr(v));
	}

	vertices() {
		return this.vitr();
	}

	edges() {
		return map((e) => [...this.endpoints(e), e], this.eitr());
	}

	incident(u) {
		return map((e) => [...this.endpoints(e), e], this.iitr(u));
	}

	endpoints(_e) {
		throw new NotImplementedError();
	}

	initr(_) {
		throw new NotImplementedError();
	}

	outitr(_) {
		throw new NotImplementedError();
	}

	dpitr(u) {
		return map((e) => other(this.endpoints(e), u), this.initr(u));
	}

	dsitr(u) {
		return map((e) => other(this.endpoints(e), u), this.outitr(u));
	}

	ingoing(u) {
		return map((e) => [other(this.endpoints(e), u), u, e], this.initr(u));
	}

	outgoing(u) {
		return map((e) => [u, other(this.endpoints(e), u), e], this.outitr(u));
	}

	reverse() {
		throw new NotImplementedError();
	}
}

import Graph from './Graph.js';

export default class AbstractUndirectedGraph extends Graph {
	initr(u) {
		return this.iitr(u);
	}

	outitr(u) {
		return this.iitr(u);
	}

	reverse() {}
}

import AbstractDirectedGraph from './AbstractDirectedGraph.js';

const isEdge =
	(u, v) =>
	([x, y]) =>
		u === x && v === y;

export default class DirectedGraph extends AbstractDirectedGraph {
	eadd(u, v) {
		const existingEdge = this.E.find(isEdge(u, v));
		if (existingEdge !== undefined) return existingEdge;
		return super.eadd(u, v);
	}
}

import {filter} from '@iterable-iterator/filter';
import {iter} from '@iterable-iterator/iter';
import {list} from '@iterable-iterator/list';

import AbstractGraph from './AbstractGraph.js';

export default class Graph extends AbstractGraph {
	constructor() {
		super();
		this.V = [];
		this.E = [];
	}

	vadd(value) {
		const v = {value};
		this.V.push(v);
		return v;
	}

	vdel(v) {
		this.V = list(filter((u) => u !== v, this.V));
		this.E = list(filter(([t, u]) => t !== v && u !== v, this.E));
	}

	eadd(u, v) {
		const insertedEdge = [u, v];
		this.E.push(insertedEdge);
		return insertedEdge;
	}

	edel(e) {
		this.E = list(filter((x) => x !== e, this.E));
	}

	vitr() {
		return iter(this.V);
	}

	eitr() {
		return iter(this.E);
	}

	endpoints([u, v]) {
		return [u, v];
	}
}

import AbstractUndirectedGraph from './AbstractUndirectedGraph.js';

const isEdge =
	(u, v) =>
	([x, y]) => {
		return (u === x && v === y) || (u === y && v === x);
	};

export default class UndirectedGraph extends AbstractUndirectedGraph {
	eadd(u, v) {
		const existingEdge = this.E.find(isEdge(u, v));
		if (existingEdge !== undefined) return existingEdge;
		return super.eadd(u, v);
	}
}

export {default as AbstractDirectedGraph} from './implementation/AbstractDirectedGraph.js';
export {default as AbstractGraph} from './implementation/AbstractGraph.js';
export {default as AbstractUndirectedGraph} from './implementation/AbstractUndirectedGraph.js';
export {default as DirectedGraph} from './implementation/DirectedGraph.js';
export {default as DirectedMultiGraph} from './implementation/DirectedMultiGraph.js';
export {default as Graph} from './implementation/Graph.js';
export {default as UndirectedGraph} from './implementation/UndirectedGraph.js';
export {default as UndirectedMultiGraph} from './implementation/UndirectedMultiGraph.js';
export {default as digraph} from './spec/digraph.js';
export {default as graph} from './spec/graph.js';
export {default as methods} from './spec/methods.js';
export {default as multiDigraph} from './spec/multiDigraph.js';
export {default as multiGraph} from './spec/multiGraph.js';

import {list} from '@iterable-iterator/list';
import {range} from '@iterable-iterator/range';
import {map} from '@iterable-iterator/map';
import {chain} from '@iterable-iterator/chain';
import {exhaust} from '@iterable-iterator/consume';
import {all} from '@iterable-iterator/reduce';
import {len, isEmpty} from '@iterable-iterator/cardinality';
import {set} from '@collection-abstraction/set';

import methods from './methods.js';

export default function digraph(test, title, Constructor) {
	methods(test, title, Constructor);

	test('graph-spec : DiGraph simple test > ' + title, (t) => {
		const G = new Constructor();

		const u = G.vadd('A');
		const v = G.vadd('B');

		const uv = G.eadd(u, v);

		t.true(set(G.vitr()).isequal(G.vertices()));

		t.true(set([u, v]).isequal(G.vitr()));
		let [a, b] = G.edges().next().value;
		t.deepEqual([a, b], [u, v]);

		G.reverse();

		t.true(set([u, v]).isequal(G.vitr()));
		[a, b] = G.edges().next().value;
		t.deepEqual([a, b], [v, u]);

		G.edel(uv);
		t.true(isEmpty(G.eitr()));

		G.vdel(u);
		G.vdel(v);
		t.true(isEmpty(G.vitr()));
	});

	test('graph-spec : DiGraph extensive test > ' + title, (t) => {
		const G = new Constructor();

		const n = 10;

		let V;
		let E;

		const init = () => {
			const V = list(map((i) => G.vadd(i), range(n)));
			t.true(set(G.vitr()).isequal(G.vertices()));

			const E = [
				G.eadd(V[0], V[1]),
				G.eadd(V[0], V[2]),
				G.eadd(V[0], V[3]),

				G.eadd(V[4], V[1]),
				G.eadd(V[4], V[2]),
				G.eadd(V[4], V[3]),

				G.eadd(V[5], V[6]),
				G.eadd(V[5], V[7]),
				G.eadd(V[5], V[8]),

				G.eadd(V[9], V[6]),
				G.eadd(V[9], V[7]),
				G.eadd(V[9], V[8]),

				G.eadd(V[0], V[1]),
				G.eadd(V[0], V[2]),
				G.eadd(V[0], V[3]),
			];

			return [V, E];
		};

		const delete_all_edges = () => exhaust(map(G.edel.bind(G), E));
		const delete_all_vertices = () => exhaust(map(G.vdel.bind(G), V));

		[V, E] = init();

		t.is(len(G.vitr()), 10);
		t.is(len(G.eitr()), 12);

		delete_all_edges();

		t.is(len(G.vitr()), 10);
		t.true(isEmpty(G.eitr()));

		delete_all_vertices();

		t.true(isEmpty(G.vitr()));
		t.true(isEmpty(G.eitr()));

		[V, E] = init();

		t.is(len(G.vitr()), 10);
		t.is(len(G.eitr()), 12);

		delete_all_vertices();

		t.true(isEmpty(G.vitr()));
		t.true(isEmpty(G.eitr()));

		[V, E] = init();

		t.is(len(G.iitr(V[0])), 3);
		t.is(len(G.iitr(V[1])), 2);
		t.is(len(G.iitr(V[2])), 2);
		t.is(len(G.iitr(V[3])), 2);
		t.is(len(G.iitr(V[4])), 3);
		t.is(len(G.iitr(V[5])), 3);
		t.is(len(G.iitr(V[6])), 2);
		t.is(len(G.iitr(V[7])), 2);
		t.is(len(G.iitr(V[8])), 2);
		t.is(len(G.iitr(V[9])), 3);

		t.true(isEmpty(G.initr(V[0])));
		t.is(len(G.initr(V[1])), 2);
		t.is(len(G.initr(V[2])), 2);
		t.is(len(G.initr(V[3])), 2);
		t.true(isEmpty(G.initr(V[4])));
		t.true(isEmpty(G.initr(V[5])));
		t.is(len(G.initr(V[6])), 2);
		t.is(len(G.initr(V[7])), 2);
		t.is(len(G.initr(V[8])), 2);
		t.true(isEmpty(G.initr(V[9])));

		t.is(len(G.outitr(V[0])), 3);
		t.true(isEmpty(G.outitr(V[1])));
		t.true(isEmpty(G.outitr(V[2])));
		t.true(isEmpty(G.outitr(V[3])));
		t.is(len(G.outitr(V[4])), 3);
		t.is(len(G.outitr(V[5])), 3);
		t.true(isEmpty(G.outitr(V[6])));
		t.true(isEmpty(G.outitr(V[7])));
		t.true(isEmpty(G.outitr(V[8])));
		t.is(len(G.outitr(V[9])), 3);

		G.reverse();

		t.is(len(G.iitr(V[0])), 3);
		t.is(len(G.iitr(V[1])), 2);
		t.is(len(G.iitr(V[2])), 2);
		t.is(len(G.iitr(V[3])), 2);
		t.is(len(G.iitr(V[4])), 3);
		t.is(len(G.iitr(V[5])), 3);
		t.is(len(G.iitr(V[6])), 2);
		t.is(len(G.iitr(V[7])), 2);
		t.is(len(G.iitr(V[8])), 2);
		t.is(len(G.iitr(V[9])), 3);

		t.true(isEmpty(G.outitr(V[0])));
		t.is(len(G.outitr(V[1])), 2);
		t.is(len(G.outitr(V[2])), 2);
		t.is(len(G.outitr(V[3])), 2);
		t.true(isEmpty(G.outitr(V[4])));
		t.true(isEmpty(G.outitr(V[5])));
		t.is(len(G.outitr(V[6])), 2);
		t.is(len(G.outitr(V[7])), 2);
		t.is(len(G.outitr(V[8])), 2);
		t.true(isEmpty(G.outitr(V[9])));

		t.is(len(G.initr(V[0])), 3);
		t.true(isEmpty(G.initr(V[1])));
		t.true(isEmpty(G.initr(V[2])));
		t.true(isEmpty(G.initr(V[3])));
		t.is(len(G.initr(V[4])), 3);
		t.is(len(G.initr(V[5])), 3);
		t.true(isEmpty(G.initr(V[6])));
		t.true(isEmpty(G.initr(V[7])));
		t.true(isEmpty(G.initr(V[8])));
		t.is(len(G.initr(V[9])), 3);

		t.true(set(G.nitr(V[0])).isequal([V[1], V[2], V[3]]));
		t.true(set(G.nitr(V[1])).isequal([V[0], V[4]]));
		t.true(set(G.nitr(V[2])).isequal([V[0], V[4]]));
		t.true(set(G.nitr(V[3])).isequal([V[0], V[4]]));
		t.true(set(G.nitr(V[4])).isequal([V[1], V[2], V[3]]));
		t.true(set(G.nitr(V[5])).isequal([V[6], V[7], V[8]]));
		t.true(set(G.nitr(V[6])).isequal([V[5], V[9]]));
		t.true(set(G.nitr(V[7])).isequal([V[5], V[9]]));
		t.true(set(G.nitr(V[8])).isequal([V[5], V[9]]));
		t.true(set(G.nitr(V[9])).isequal([V[6], V[7], V[8]]));

		t.true(set(G.dsitr(V[0])).isequal([]));
		t.true(set(G.dsitr(V[1])).isequal([V[0], V[4]]));
		t.true(set(G.dsitr(V[2])).isequal([V[0], V[4]]));
		t.true(set(G.dsitr(V[3])).isequal([V[0], V[4]]));
		t.true(set(G.dsitr(V[4])).isequal([]));
		t.true(set(G.dsitr(V[5])).isequal([]));
		t.true(set(G.dsitr(V[6])).isequal([V[5], V[9]]));
		t.true(set(G.dsitr(V[7])).isequal([V[5], V[9]]));
		t.true(set(G.dsitr(V[8])).isequal([V[5], V[9]]));
		t.true(set(G.dsitr(V[9])).isequal([]));

		t.true(set(G.dpitr(V[0])).isequal([V[1], V[2], V[3]]));
		t.true(set(G.dpitr(V[1])).isequal([]));
		t.true(set(G.dpitr(V[2])).isequal([]));
		t.true(set(G.dpitr(V[3])).isequal([]));
		t.true(set(G.dpitr(V[4])).isequal([V[1], V[2], V[3]]));
		t.true(set(G.dpitr(V[5])).isequal([V[6], V[7], V[8]]));
		t.true(set(G.dpitr(V[6])).isequal([]));
		t.true(set(G.dpitr(V[7])).isequal([]));
		t.true(set(G.dpitr(V[8])).isequal([]));
		t.true(set(G.dpitr(V[9])).isequal([V[6], V[7], V[8]]));

		t.is(len(G.edges()), 12, 'G.edges( ) length');

		const edges = set(E);

		for (const [u, v, e] of G.edges()) {
			t.true(edges.has(e));

			t.deepEqual([u, v], G.endpoints(e));

			edges.remove(e);
		}

		for (const i of range(n)) {
			t.true(all(map(([u, v]) => u === V[i] || v === V[i], G.incident(V[i]))));

			t.true(
				set(map(([_u, _v, e]) => e, G.incident(V[i]))).isequal(
					chain(
						map(([_u, _v, e]) => e, G.ingoing(V[i])),
						map(([_u, _v, e]) => e, G.outgoing(V[i])),
					),
				),
			);

			t.true(
				set(map(([, v]) => v, G.incident(V[i]))).isequal(
					chain(
						map(([, v]) => v, G.ingoing(V[i])),
						map(([, v]) => v, G.outgoing(V[i])),
					),
				),
			);

			t.true(
				set(map(([u]) => u, G.incident(V[i]))).isequal(
					chain(
						map(([u]) => u, G.ingoing(V[i])),
						map(([u]) => u, G.outgoing(V[i])),
					),
				),
			);

			t.true(
				set(G.nitr(V[i])).isequal(
					map(([u, v]) => (u === V[i] ? v : u), G.incident(V[i])),
				),
			);
			t.true(set(G.dpitr(V[i])).isequal(map(([u]) => u, G.ingoing(V[i]))));
			t.true(set(G.dsitr(V[i])).isequal(map(([, v]) => v, G.outgoing(V[i]))));
		}

		delete_all_edges();

		t.is(len(G.vitr()), 10);
		t.true(isEmpty(G.eitr()));

		delete_all_vertices();

		t.true(isEmpty(G.vitr()));
		t.true(isEmpty(G.eitr()));
	});
}

import {list} from '@iterable-iterator/list';
import {range} from '@iterable-iterator/range';
import {map} from '@iterable-iterator/map';
import {chain} from '@iterable-iterator/chain';
import {exhaust} from '@iterable-iterator/consume';
import {all} from '@iterable-iterator/reduce';
import {len, isEmpty} from '@iterable-iterator/cardinality';
import {set} from '@collection-abstraction/set';

import methods from './methods.js';

export default function graph(test, title, Constructor) {
	methods(test, title, Constructor);

	test('graph-spec : Graph simple test > ' + title, (t) => {
		const G = new Constructor();

		const u = G.vadd('A');
		const v = G.vadd('B');

		const uv = G.eadd(u, v);

		t.true(set(G.vitr()).isequal(G.vertices()));

		t.true(set([u, v]).isequal(G.vitr()));
		let [a, b] = G.edges().next().value;
		t.true(set([a, b]).isequal([u, v]));

		G.reverse();

		t.true(set([u, v]).isequal(G.vitr()));
		t.is(G.eitr().next().value, uv);
		[a, b] = G.edges().next().value;
		t.true(set([a, b]).isequal([u, v]));

		const vu = G.eadd(v, u);
		t.is(len(G.eitr()), 1);
		t.is(uv, vu);

		G.edel(uv);
		t.true(isEmpty(G.eitr()));

		G.vdel(u);
		G.vdel(v);
		t.true(isEmpty(G.vitr()));
	});

	test('graph-spec : Graph #1 > ' + title, (t) => {
		const g = new Constructor();

		const v = [];
		const e = [];

		const n = 11;

		for (let i = 0; i < n; ++i) v[i] = g.vadd(i);
		t.true(set(g.vitr()).isequal(g.vertices()));

		e[1] = [];
		e[1][0] = g.eadd(v[1], v[9]);
		e[0] = [];
		e[0][0] = g.eadd(v[0], v[10]);
		e[0][1] = g.eadd(v[0], v[5]);
		e[0][2] = g.eadd(v[0], v[3]);
		e[0][3] = g.eadd(v[0], v[1]);
		e[0][4] = g.eadd(v[0], v[0]);
		e[1][1] = e[0][3];
		e[4] = [];
		e[4][0] = g.eadd(v[4], v[7]);

		const r = [0, 1, 4];

		// Tests

		let k = 0;
		let notseen = set(v);

		exhaust(
			map((j) => {
				t.true(notseen.has(j), 'vitr ' + k);
				notseen.remove(j);
				++k;
			}, g.vitr()),
		);

		k = 0;
		let alledges = e[0].concat([e[1][0]]).concat(e[4]);
		notseen = set(alledges);

		exhaust(
			map((j) => {
				t.true(notseen.has(j), 'eitr ' + k);
				notseen.remove(j);
				++k;
			}, g.eitr()),
		);

		t.is(k, alledges.length, 'check edges count before del');

		for (const m of r) {
			k = e[m].length;

			notseen = set(e[m]);

			exhaust(
				map((x) => {
					--k;
					t.true(notseen.has(x), 'iitr ' + m + ' ' + k);
					notseen.remove(x);
				}, g.iitr(v[m])),
			);
		}

		// Delete edges 1 0 , 0 2 , 4 0
		g.edel(e[1].splice(0, 1)[0]);

		g.edel(e[0].splice(2, 1)[0]);

		g.edel(e[4].splice(0, 1)[0]);

		k = 0;
		alledges = e[0].concat(e[4]);
		notseen = set(alledges);

		exhaust(
			map((j) => {
				t.true(notseen.has(j), 'eitr ' + k);
				notseen.remove(j);
				++k;
			}, g.eitr()),
		);

		t.is(k, alledges.length, 'check edges count after del');

		k = 0;
		notseen = set(map((e) => g.endpoints(e)[1], e[0]));

		exhaust(
			map((j) => {
				t.true(notseen.has(j), 'nitr ' + k);
				notseen.remove(j);
				++k;
			}, g.nitr(v[0])),
		);

		t.is(k, e[0].length, 'check neighbour count after del');

		for (const m of r) {
			k = e[m].length;

			notseen = set(e[m]);

			exhaust(
				map((x) => {
					--k;
					t.true(notseen.has(x), 'iitr ' + m + ' ' + k);
					notseen.remove(x);
				}, g.iitr(v[m])),
			);
		}

		// Delete vertex 3

		g.vdel(v.splice(3, 1)[0]);

		k = 0;
		notseen = set(v);

		exhaust(
			map((j) => {
				t.true(notseen.has(j), 'vitr ' + k);
				notseen.remove(j);
				++k;
			}, g.vitr()),
		);

		t.is(k, v.length, 'check vertex count after del');

		e[0].splice(2, 1);

		// Delete remaining edges
		for (const m of r) {
			while (e[m].length > 0) g.edel(e[m].splice(0, 1)[0]);
		}

		t.true(isEmpty(g.eitr()), 'no more edges');

		t.true(
			all(map((i) => isEmpty(g.iitr(i)), g.vitr())),
			'no more incident edges',
		);

		// Delete remaining vertices
		while (v.length > 0) g.vdel(v.splice(0, 1)[0]);

		t.true(isEmpty(g.vitr()), 'no more vertices');
	});

	test('graph-spec : Graph #2 > ' + title, (t) => {
		const g = new Constructor();

		const v = [];
		const e = [];

		const n = 11;

		for (let i = 0; i < n; ++i) v[i] = g.vadd(i);

		e[1] = [];
		e[1][0] = g.eadd(v[1], v[9]);
		e[0] = [];
		e[0][0] = g.eadd(v[0], v[10]);
		e[0][1] = g.eadd(v[0], v[5]);
		e[0][2] = g.eadd(v[0], v[3]);
		e[0][3] = g.eadd(v[0], v[1]);
		e[0][4] = g.eadd(v[0], v[0]);
		e[1][1] = e[0][3];
		e[4] = [];
		e[4][0] = g.eadd(v[4], v[7]);

		const r = [0, 1, 4];

		// Tests

		let k;
		let notseen;
		let alledges;

		k = 0;
		notseen = set(v);

		exhaust(
			map((j) => {
				t.true(notseen.has(j), 'vitr ' + k);
				notseen.remove(j);
				++k;
			}, g.vitr()),
		);

		k = 0;
		alledges = e[0].concat([e[1][0]]).concat(e[4]);
		notseen = set(alledges);

		exhaust(
			map((j) => {
				t.true(notseen.has(j), 'eitr ' + k);
				notseen.remove(j);
				++k;
			}, g.eitr()),
		);

		t.is(k, alledges.length, 'check edges count before del');

		for (const m of r) {
			k = e[m].length;

			notseen = set(e[m]);

			exhaust(
				map((x) => {
					--k;
					t.true(notseen.has(x), 'iitr ' + m + ' ' + k);
					notseen.remove(x);
				}, g.iitr(v[m])),
			);
		}

		// Delete edges 1 0 , 0 2 , 4 0
		g.edel(e[1].splice(0, 1)[0]);

		g.edel(e[0].splice(2, 1)[0]);

		g.edel(e[4].splice(0, 1)[0]);

		k = 0;
		alledges = e[0].concat(e[4]);
		notseen = set(alledges);

		exhaust(
			map((j) => {
				t.true(notseen.has(j), 'eitr ' + k);
				notseen.remove(j);
				++k;
			}, g.eitr()),
		);

		t.is(k, alledges.length, 'check edges count after del');

		k = 0;
		notseen = set(map((e) => g.endpoints(e)[1], e[0]));

		exhaust(
			map((j) => {
				t.true(notseen.has(j), 'nitr ' + k);
				notseen.remove(j);
				++k;
			}, g.nitr(v[0])),
		);

		t.is(k, e[0].length, 'check neighbour count after del');

		for (const m of r) {
			k = e[m].length;

			notseen = set(e[m]);

			exhaust(
				map((x) => {
					--k;
					t.true(notseen.has(x), 'iitr ' + m + ' ' + k);
					notseen.remove(x);
				}, g.iitr(v[m])),
			);
		}

		// Delete vertex 10

		g.vdel(v.splice(10, 1)[0]);
		e[0].splice(0, 1);

		k = 0;
		notseen = set(v);

		exhaust(
			map((j) => {
				t.true(notseen.has(j), 'vitr ' + k);
				notseen.remove(j);
				++k;
			}, g.vitr()),
		);

		t.is(k, v.length, 'check vertex count after del');

		e[0].splice(1, 1);

		// Delete remaining edges
		for (const m of r) {
			while (e[m].length > 0) g.edel(e[m].splice(0, 1)[0]);
		}

		t.true(isEmpty(g.eitr()), 'no more edges');

		t.true(
			all(map((i) => isEmpty(g.iitr(i)), g.vitr())),
			'no more incident edges',
		);

		// Delete remaining vertices
		while (v.length > 0) g.vdel(v.splice(0, 1)[0]);

		t.true(isEmpty(g.vitr()), 'no more vertices');
	});

	test('graph-spec : Graph extensive test > ' + title, (t) => {
		const G = new Constructor();

		const n = 10;

		const init = () => {
			const V = list(map((i) => G.vadd(i), range(n)));
			t.true(set(G.vitr()).isequal(G.vertices()));

			const E = [
				G.eadd(V[0], V[1]),
				G.eadd(V[0], V[2]),
				G.eadd(V[0], V[3]),

				G.eadd(V[4], V[1]),
				G.eadd(V[4], V[2]),
				G.eadd(V[4], V[3]),

				G.eadd(V[5], V[6]),
				G.eadd(V[5], V[7]),
				G.eadd(V[5], V[8]),

				G.eadd(V[9], V[6]),
				G.eadd(V[9], V[7]),
				G.eadd(V[9], V[8]),

				G.eadd(V[0], V[1]),
				G.eadd(V[0], V[2]),
				G.eadd(V[0], V[3]),
			];

			return [V, E];
		};

		const delete_all_edges = () => exhaust(map(G.edel.bind(G), E));
		const delete_all_vertices = () => exhaust(map(G.vdel.bind(G), V));

		let [V, E] = init();

		t.is(len(G.vitr()), 10);
		t.is(len(G.eitr()), 12);

		delete_all_edges();

		t.is(len(G.vitr()), 10);
		t.true(isEmpty(G.eitr()));

		delete_all_vertices();

		t.true(isEmpty(G.vitr()));
		t.true(isEmpty(G.eitr()));

		[V, E] = init();

		t.is(len(G.vitr()), 10);
		t.is(len(G.eitr()), 12);

		delete_all_vertices();

		t.true(isEmpty(G.vitr()));
		t.true(isEmpty(G.eitr()));

		[V, E] = init();

		t.is(len(G.iitr(V[0])), 3);
		t.is(len(G.iitr(V[1])), 2);
		t.is(len(G.iitr(V[2])), 2);
		t.is(len(G.iitr(V[3])), 2);
		t.is(len(G.iitr(V[4])), 3);
		t.is(len(G.iitr(V[5])), 3);
		t.is(len(G.iitr(V[6])), 2);
		t.is(len(G.iitr(V[7])), 2);
		t.is(len(G.iitr(V[8])), 2);
		t.is(len(G.iitr(V[9])), 3);

		t.is(len(G.initr(V[0])), 3);
		t.is(len(G.initr(V[1])), 2);
		t.is(len(G.initr(V[2])), 2);
		t.is(len(G.initr(V[3])), 2);
		t.is(len(G.initr(V[4])), 3);
		t.is(len(G.initr(V[5])), 3);
		t.is(len(G.initr(V[6])), 2);
		t.is(len(G.initr(V[7])), 2);
		t.is(len(G.initr(V[8])), 2);
		t.is(len(G.initr(V[9])), 3);

		t.is(len(G.outitr(V[0])), 3);
		t.is(len(G.outitr(V[1])), 2);
		t.is(len(G.outitr(V[2])), 2);
		t.is(len(G.outitr(V[3])), 2);
		t.is(len(G.outitr(V[4])), 3);
		t.is(len(G.outitr(V[5])), 3);
		t.is(len(G.outitr(V[6])), 2);
		t.is(len(G.outitr(V[7])), 2);
		t.is(len(G.outitr(V[8])), 2);
		t.is(len(G.outitr(V[9])), 3);

		G.reverse();

		t.is(len(G.iitr(V[0])), 3);
		t.is(len(G.iitr(V[1])), 2);
		t.is(len(G.iitr(V[2])), 2);
		t.is(len(G.iitr(V[3])), 2);
		t.is(len(G.iitr(V[4])), 3);
		t.is(len(G.iitr(V[5])), 3);
		t.is(len(G.iitr(V[6])), 2);
		t.is(len(G.iitr(V[7])), 2);
		t.is(len(G.iitr(V[8])), 2);
		t.is(len(G.iitr(V[9])), 3);

		t.is(len(G.outitr(V[0])), 3);
		t.is(len(G.outitr(V[1])), 2);
		t.is(len(G.outitr(V[2])), 2);
		t.is(len(G.outitr(V[3])), 2);
		t.is(len(G.outitr(V[4])), 3);
		t.is(len(G.outitr(V[5])), 3);
		t.is(len(G.outitr(V[6])), 2);
		t.is(len(G.outitr(V[7])), 2);
		t.is(len(G.outitr(V[8])), 2);
		t.is(len(G.outitr(V[9])), 3);

		t.is(len(G.initr(V[0])), 3);
		t.is(len(G.initr(V[1])), 2);
		t.is(len(G.initr(V[2])), 2);
		t.is(len(G.initr(V[3])), 2);
		t.is(len(G.initr(V[4])), 3);
		t.is(len(G.initr(V[5])), 3);
		t.is(len(G.initr(V[6])), 2);
		t.is(len(G.initr(V[7])), 2);
		t.is(len(G.initr(V[8])), 2);
		t.is(len(G.initr(V[9])), 3);

		t.true(set(G.nitr(V[0])).isequal([V[1], V[2], V[3]]));
		t.true(set(G.nitr(V[1])).isequal([V[0], V[4]]));
		t.true(set(G.nitr(V[2])).isequal([V[0], V[4]]));
		t.true(set(G.nitr(V[3])).isequal([V[0], V[4]]));
		t.true(set(G.nitr(V[4])).isequal([V[1], V[2], V[3]]));
		t.true(set(G.nitr(V[5])).isequal([V[6], V[7], V[8]]));
		t.true(set(G.nitr(V[6])).isequal([V[5], V[9]]));
		t.true(set(G.nitr(V[7])).isequal([V[5], V[9]]));
		t.true(set(G.nitr(V[8])).isequal([V[5], V[9]]));
		t.true(set(G.nitr(V[9])).isequal([V[6], V[7], V[8]]));

		t.true(set(G.dsitr(V[0])).isequal([V[1], V[2], V[3]]));
		t.true(set(G.dsitr(V[1])).isequal([V[0], V[4]]));
		t.true(set(G.dsitr(V[2])).isequal([V[0], V[4]]));
		t.true(set(G.dsitr(V[3])).isequal([V[0], V[4]]));
		t.true(set(G.dsitr(V[4])).isequal([V[1], V[2], V[3]]));
		t.true(set(G.dsitr(V[5])).isequal([V[6], V[7], V[8]]));
		t.true(set(G.dsitr(V[6])).isequal([V[5], V[9]]));
		t.true(set(G.dsitr(V[7])).isequal([V[5], V[9]]));
		t.true(set(G.dsitr(V[8])).isequal([V[5], V[9]]));
		t.true(set(G.dsitr(V[9])).isequal([V[6], V[7], V[8]]));

		t.true(set(G.dpitr(V[0])).isequal([V[1], V[2], V[3]]));
		t.true(set(G.dpitr(V[1])).isequal([V[0], V[4]]));
		t.true(set(G.dpitr(V[2])).isequal([V[0], V[4]]));
		t.true(set(G.dpitr(V[3])).isequal([V[0], V[4]]));
		t.true(set(G.dpitr(V[4])).isequal([V[1], V[2], V[3]]));
		t.true(set(G.dpitr(V[5])).isequal([V[6], V[7], V[8]]));
		t.true(set(G.dpitr(V[6])).isequal([V[5], V[9]]));
		t.true(set(G.dpitr(V[7])).isequal([V[5], V[9]]));
		t.true(set(G.dpitr(V[8])).isequal([V[5], V[9]]));
		t.true(set(G.dpitr(V[9])).isequal([V[6], V[7], V[8]]));

		t.is(len(G.edges()), 12, 'G.edges( ) length');

		const edges = set(E);

		for (const [u, v, e] of G.edges()) {
			t.true(edges.has(e));

			t.true(set([u, v]).isequal(G.endpoints(e)));

			edges.remove(e);
		}

		for (const i of range(n)) {
			t.true(all(map(([u, v]) => u === V[i] || v === V[i], G.incident(V[i]))));

			t.true(
				set(map(([_u, _v, e]) => e, G.incident(V[i]))).isequal(
					chain(
						map(([_u, _v, e]) => e, G.ingoing(V[i])),
						map(([_u, _v, e]) => e, G.outgoing(V[i])),
					),
				),
			);

			t.true(
				set(
					chain(
						map(([, v]) => v, G.incident(V[i])),
						map(([u]) => u, G.incident(V[i])),
					),
				).isequal(
					chain(
						[V[i]],
						map(([u]) => u, G.ingoing(V[i])),
						map(([, v]) => v, G.outgoing(V[i])),
					),
				),
			);

			t.true(
				set(G.nitr(V[i])).isequal(
					map(([u, v]) => (u === V[i] ? v : u), G.incident(V[i])),
				),
			);
			t.true(set(G.dpitr(V[i])).isequal(map(([u]) => u, G.ingoing(V[i]))));
			t.true(set(G.dsitr(V[i])).isequal(map(([, v]) => v, G.outgoing(V[i]))));
		}

		delete_all_edges();

		t.is(len(G.vitr()), 10);
		t.true(isEmpty(G.eitr()));

		delete_all_vertices();

		t.true(isEmpty(G.vitr()));
		t.true(isEmpty(G.eitr()));
	});
}

import {isFunction} from '@functional-abstraction/type';

export default function methods(test, title, Constructor) {
	test('graph-spec : check existence of methods > ' + title, (t) => {
		t.true(isFunction(Constructor), 'constructor');

		const G = new Constructor();

		t.true(isFunction(G.vadd), 'vadd');
		t.true(isFunction(G.vdel), 'vdel');
		t.true(isFunction(G.eadd), 'eadd');
		t.true(isFunction(G.edel), 'edel');
		t.true(G.vitr !== null, 'vitr');
		t.true(G.eitr !== null, 'eitr');
		t.true(G.iitr !== null, 'iitr');
		t.true(G.initr !== null, 'initr');
		t.true(G.outitr !== null, 'outitr');
		t.true(G.nitr !== null, 'nitr');
		t.true(G.dsitr !== null, 'dsitr');
		t.true(G.dpitr !== null, 'dpitr');
		t.true(G.vertices !== null, 'vertices');
		t.true(G.edges !== null, 'edges');
		t.true(G.incident !== null, 'incident');
		t.true(G.ingoing !== null, 'ingoing');
		t.true(G.outgoing !== null, 'outgoing');
		t.true(G.endpoints !== null, 'endpoints');
		t.true(G.reverse !== null, 'reverse');
	});
}

import {list} from '@iterable-iterator/list';
import {range} from '@iterable-iterator/range';
import {map} from '@iterable-iterator/map';
import {chain} from '@iterable-iterator/chain';
import {exhaust} from '@iterable-iterator/consume';
import {all} from '@iterable-iterator/reduce';
import {len, isEmpty} from '@iterable-iterator/cardinality';
import {set} from '@collection-abstraction/set';

import methods from './methods.js';

export default function multiDigraph(test, title, Constructor) {
	methods(test, title, Constructor);

	test('graph-spec : MultiDiGraph simple test > ' + title, (t) => {
		const G = new Constructor();

		const u = G.vadd('A');
		const v = G.vadd('B');

		const uv = G.eadd(u, v);

		t.true(set(G.vitr()).isequal(G.vertices()));

		t.true(set([u, v]).isequal(G.vitr()));
		let [a, b] = G.edges().next().value;
		t.deepEqual([a, b], [u, v]);

		G.reverse();

		t.true(set([u, v]).isequal(G.vitr()));
		[a, b] = G.edges().next().value;
		t.deepEqual([a, b], [v, u]);

		G.edel(uv);
		t.true(isEmpty(G.eitr()));

		G.vdel(u);
		G.vdel(v);
		t.true(isEmpty(G.vitr()));
	});

	test('graph-spec : MultiDiGraph extensive test > ' + title, (t) => {
		const G = new Constructor();

		const n = 10;

		let V;
		let E;

		const init = () => {
			const V = list(map((i) => G.vadd(i), range(n)));
			t.true(set(G.vitr()).isequal(G.vertices()));

			const E = [
				G.eadd(V[0], V[1]),
