        "presets": [
          [
            "@babel/preset-env",
            {
              "targets": [
                "defaults",
                "maintained node versions"
              ]
            }
          ],
          "babel-preset-power-assert"
        ]
      },
      "production": {
        "presets": [
          [
            "@babel/preset-env",
            {
              "targets": [
                "defaults",
                "maintained node versions"
              ]
            }
          ]
        ],
        "plugins": [
          "babel-plugin-unassert"
        ]
      }
    }
  },
  "lint-staged": {
    "*.js": [
      "npm run lint-and-fix"
    ],
    "package.json": [
      "npm run lint-config-and-fix"
    ]
  },
  "prettier": {
    "trailingComma": "all"
  },
  "xo": {
    "prettier": true,
    "plugins": [
      "unicorn"
    ],
    "rules": {
